option.value:
export default {
  plugins: {
    tailwindcss: {},
    autoprefixer: {},
  },
}

module.exports = {
  plugins: {
    tailwindcss: {},
    autoprefixer: {},
  },
}

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useDashboard } from '@/hooks/useFinance';
import DebtSavingsChart from './DebtSavingsChart';
import LoanTable from './LoanTable';
import SummaryCards from './SummaryCards';
import { formatCurrency, formatDate } from '@/utils/calculations';
import LoanProjectionChart from './LoanProjectionChart';
import PartPaymentForm from '@/components/LoanManagement/PartPaymentForm';

const Dashboard: React.FC = () => {
  const { dashboardData, lastRefresh, addPartPayment } = useDashboard();
  const [partPaymentLoanId, setPartPaymentLoanId] = useState<string | null>(null);

  const selectedLoan = partPaymentLoanId
    ? dashboardData.loans.find(l => l.id === partPaymentLoanId) || null
    : null;

  return (
    <div className="space-y-8">
      {/* Auto-refresh indicator */}
      {lastRefresh && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-card text-center"
        >
          <p className="text-white/70 text-sm">
            Last auto-refreshed on {formatDate(lastRefresh)} • Next refresh: 5th of next month
          </p>
        </motion.div>
      )}

      {/* Summary Cards */}
      <SummaryCards dashboardData={dashboardData} />

      {/* Charts Section */}
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.2 }}
        >
          <DebtSavingsChart dashboardData={dashboardData} />
        </motion.div>

        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.3 }}
          className="glass-card"
        >
          <h3 className="text-xl font-semibold text-white mb-4">Financial Overview</h3>
          <div className="space-y-4">
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Total Outstanding Debt</span>
              <span className="text-2xl font-bold text-red-400">
                {formatCurrency(dashboardData.totalOutstandingDebt)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Total Savings</span>
              <span className="text-2xl font-bold text-green-400">
                {formatCurrency(dashboardData.totalSavings)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Monthly EMI</span>
              <span className="text-xl font-semibold text-orange-400">
                {formatCurrency(dashboardData.monthlyEmi)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Next EMI Date</span>
              <span className="text-lg font-medium text-blue-400">
                {formatDate(dashboardData.nextEmiDate)}
              </span>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Payment Projection Chart */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.35 }}
      >
        <LoanProjectionChart dashboardData={dashboardData} />
      </motion.div>

      {/* Loan Table with Part Payment action */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
      >
        <LoanTable
          loans={dashboardData.loans}
          onPartPayment={(loan) => setPartPaymentLoanId(loan.id)}
          showActions={true}
        />
      </motion.div>

      {/* Part Payment Modal */}
      {selectedLoan && partPaymentLoanId && (
        <PartPaymentForm
          loanId={partPaymentLoanId}
          loan={selectedLoan}
          onSubmit={(data) => {
            addPartPayment(partPaymentLoanId, data);
            setPartPaymentLoanId(null);
          }}
          onCancel={() => setPartPaymentLoanId(null)}
        />
      )}
    </div>
  );
};

export default Dashboard;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useDashboard } from '@/hooks/useFinance';
import DebtSavingsChart from './DebtSavingsChart';
import LoanTable from './LoanTable';
import SummaryCards from './SummaryCards';
import { formatCurrency, formatDate } from '@/utils/calculations';
import LoanProjectionChart from './LoanProjectionChart';
import PartPaymentForm from '@/components/LoanManagement/PartPaymentForm';

const Dashboard: React.FC = () => {
  const { dashboardData, lastRefresh, addPartPayment, removeLastPartPayment } = useDashboard();
  const [partPaymentLoanId, setPartPaymentLoanId] = useState<string | null>(null);

  const selectedLoan = partPaymentLoanId
    ? dashboardData.loans.find(l => l.id === partPaymentLoanId) || null
    : null;

  return (
    <div className="space-y-8">
      {/* Auto-refresh indicator */}
      {lastRefresh && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-card text-center"
        >
          <p className="text-white/70 text-sm">
            Last auto-refreshed on {formatDate(lastRefresh)} • Next refresh: 5th of next month
          </p>
        </motion.div>
      )}

      {/* Summary Cards */}
      <SummaryCards dashboardData={dashboardData} />

      {/* Charts Section */}
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.2 }}
        >
          <DebtSavingsChart dashboardData={dashboardData} />
        </motion.div>

        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.3 }}
          className="glass-card"
        >
          <h3 className="text-xl font-semibold text-white mb-4">Financial Overview</h3>
          <div className="space-y-4">
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Total Outstanding Debt</span>
              <span className="text-2xl font-bold text-red-400">
                {formatCurrency(dashboardData.totalOutstandingDebt)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Total Savings</span>
              <span className="text-2xl font-bold text-green-400">
                {formatCurrency(dashboardData.totalSavings)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Monthly EMI</span>
              <span className="text-xl font-semibold text-orange-400">
                {formatCurrency(dashboardData.monthlyEmi)}
              </span>
            </div>
            <div className="flex justify-between items-center p-4 bg-white/5 rounded-lg">
              <span className="text-white/70">Next EMI Date</span>
              <span className="text-lg font-medium text-blue-400">
                {formatDate(dashboardData.nextEmiDate)}
              </span>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Payment Projection Chart */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.35 }}
      >
        <LoanProjectionChart dashboardData={dashboardData} />
      </motion.div>

      {/* Loan Table with Part Payment action */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
      >
        <LoanTable
          loans={dashboardData.loans}
          onPartPayment={(loan) => setPartPaymentLoanId(loan.id)}
          onUndoLastPartPayment={(loan) => {
            if (confirm('Undo the last part payment for this loan?')) {
              removeLastPartPayment(loan.id);
            }
          }}
          showActions={true}
        />
      </motion.div>

      {/* Part Payment Modal */}
      {selectedLoan && partPaymentLoanId && (
        <PartPaymentForm
          loanId={partPaymentLoanId}
          loan={selectedLoan}
          onSubmit={(data) => {
            addPartPayment(partPaymentLoanId, data);
            setPartPaymentLoanId(null);
          }}
          onCancel={() => setPartPaymentLoanId(null)}
        />
      )}
    </div>
  );
};

export default Dashboard;

import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import { DashboardData } from '@/types';
import { formatCurrency } from '@/utils/calculations';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

interface DebtSavingsChartProps {
  dashboardData: DashboardData;
}

const DebtSavingsChart: React.FC<DebtSavingsChartProps> = ({ dashboardData }) => {
  const { totalOutstandingDebt, totalSavings } = dashboardData;

  // Doughnut chart data for debt vs savings
  const doughnutData = {
    labels: ['Outstanding Debt', 'Total Savings'],
    datasets: [
      {
        data: [totalOutstandingDebt, totalSavings],
        backgroundColor: [
          'rgba(239, 68, 68, 0.8)', // Red for debt
          'rgba(34, 197, 94, 0.8)', // Green for savings
        ],
        borderColor: [
          'rgba(239, 68, 68, 1)',
          'rgba(34, 197, 94, 1)',
        ],
        borderWidth: 2,
      },
    ],
  };

  // Bar chart data for loan breakdown
  const barData = {
    labels: dashboardData.loans.filter(loan => loan.isActive).map(loan => loan.name),
    datasets: [
      {
        label: 'Outstanding Amount',
        data: dashboardData.loans
          .filter(loan => loan.isActive)
          .map(loan => loan.currentPrincipal),
        backgroundColor: 'rgba(147, 51, 234, 0.8)',
        borderColor: 'rgba(147, 51, 234, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        labels: {
          color: 'rgba(255, 255, 255, 0.8)',
          font: {
            size: 12,
          },
        },
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: 'white',
        bodyColor: 'white',
        callbacks: {
          label: function(context: any) {
            return `${context.label}: ${formatCurrency(context.parsed || context.raw)}`;
          },
        },
      },
    },
    scales: {
      y: {
        ticks: {
          color: 'rgba(255, 255, 255, 0.6)',
          callback: function(value: any) {
            return formatCurrency(value);
          },
        },
        grid: {
          color: 'rgba(255, 255, 255, 0.1)',
        },
      },
      x: {
        ticks: {
          color: 'rgba(255, 255, 255, 0.6)',
        },
        grid: {
          color: 'rgba(255, 255, 255, 0.1)',
        },
      },
    },
  };

  const doughnutOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom' as const,
        labels: {
          color: 'rgba(255, 255, 255, 0.8)',
          padding: 20,
          font: {
            size: 12,
          },
        },
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: 'white',
        bodyColor: 'white',
        callbacks: {
          label: function(context: any) {
            const total = context.dataset.data.reduce((a: number, b: number) => a + b, 0);
            const percentage = ((context.raw / total) * 100).toFixed(1);
            return `${context.label}: ${formatCurrency(context.raw)} (${percentage}%)`;
          },
        },
      },
    },
  };

  return (
    <div className="glass-card">
      <h3 className="text-xl font-semibold text-white mb-6">Financial Overview</h3>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Debt vs Savings Doughnut Chart */}
        <div className="h-64">
          <h4 className="text-lg font-medium text-white/90 mb-4 text-center">
            Debt vs Savings
          </h4>
          {totalOutstandingDebt > 0 || totalSavings > 0 ? (
            <Doughnut data={doughnutData} options={doughnutOptions} />
          ) : (
            <div className="flex items-center justify-center h-full text-white/60">
              No data available
            </div>
          )}
        </div>

        {/* Loan Breakdown Bar Chart */}
        <div className="h-64">
          <h4 className="text-lg font-medium text-white/90 mb-4 text-center">
            Loan Breakdown
          </h4>
          {dashboardData.loans.filter(loan => loan.isActive).length > 0 ? (
            <Bar data={barData} options={chartOptions} />
          ) : (
            <div className="flex items-center justify-center h-full text-white/60">
              No active loans
            </div>
          )}
        </div>
      </div>

      {/* Net Worth Indicator */}
      <div className="mt-6 p-4 bg-white/5 rounded-lg">
        <div className="text-center">
          <p className="text-white/70 text-sm">Net Worth</p>
          <p className={`text-2xl font-bold ${
            totalSavings - totalOutstandingDebt >= 0 ? 'text-green-400' : 'text-red-400'
          }`}>
            {formatCurrency(totalSavings - totalOutstandingDebt)}
          </p>
        </div>
      </div>
    </div>
  );
};

export default DebtSavingsChart;

import React from 'react';
import { motion } from 'framer-motion';
import { Loan } from '@/types';
import { calculateLoanDetails, formatCurrency, formatDate } from '@/utils/calculations';
import { Edit, Trash2, DollarSign, TrendingDown, RotateCcw } from 'lucide-react';

interface LoanTableProps {
  loans: Loan[];
  onEdit?: (loan: Loan) => void;
  onDelete?: (loanId: string) => void;
  onPartPayment?: (loan: Loan) => void;
  onUndoLastPartPayment?: (loan: Loan) => void;
  showActions?: boolean;
}

const LoanTable: React.FC<LoanTableProps> = ({
  loans,
  onEdit,
  onDelete,
  onPartPayment,
  onUndoLastPartPayment,
  showActions = false
}) => {
  const activeLoans = loans.filter(loan => loan.isActive);

  if (activeLoans.length === 0) {
    return (
      <div className="glass-card text-center py-12">
        <DollarSign className="w-16 h-16 text-white/30 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-white/70 mb-2">No Active Loans</h3>
        <p className="text-white/50">Add your first loan to get started with tracking</p>
      </div>
    );
  }

  return (
    <div className="glass-card overflow-hidden">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-semibold text-white">Loan Details</h3>
        <span className="text-white/70 text-sm">
          {activeLoans.length} active loan{activeLoans.length !== 1 ? 's' : ''}
        </span>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="border-b border-white/10">
              <th className="text-left py-4 px-6 text-white/90 font-semibold">Loan Name</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Principal Amount</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Interest Rate</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">EMI Amount</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">EMIs Remaining</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Next EMI Date</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Outstanding</th>
              {showActions && (
                <th className="text-center py-4 px-6 text-white/90 font-semibold">Actions</th>
              )}
            </tr>
          </thead>
          <tbody>
            {activeLoans.map((loan, index) => {
              const details = calculateLoanDetails(loan);
              return (
                <motion.tr
                  key={loan.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.05 }}
                  className="border-b border-white/5 hover:bg-white/5 transition-colors"
                >
                  <td className="py-4 px-6">
                    <div>
                      <span className="text-white font-medium">{loan.name}</span>
                      {loan.partPayments.length > 0 && (
                        <div className="text-xs text-green-400 mt-1">
                          {loan.partPayments.length} part payment{loan.partPayments.length !== 1 ? 's' : ''}
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatCurrency(loan.principalAmount)}
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {Number(loan.interestRate).toFixed(2)}%
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatCurrency(details.emiAmount)}
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    <span className={`${details.remainingEmis <= 12 ? 'text-orange-400' : 'text-white/80'}`}>
                      {details.remainingEmis}
                    </span>
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatDate(details.nextEmiDate)}
                  </td>
                  <td className="py-4 px-6 text-right">
                    <span className="text-red-400 font-semibold">
                      {formatCurrency(Number(loan.currentPrincipal))}
                    </span>
                  </td>
                  {showActions && (
                    <td className="py-4 px-6">
                      <div className="flex items-center justify-center space-x-2">
                        {onEdit && (
                          <button
                            onClick={() => onEdit?.(loan)}
                            className="p-2 text-white/60 hover:text-blue-400 transition-colors"
                            title="Edit Loan"
                          >
                            <Edit className="w-4 h-4" />
                          </button>
                        )}
                        {onPartPayment && (
                          <button
                            onClick={() => onPartPayment?.(loan)}
                            className="p-2 text-white/60 hover:text-green-400 transition-colors"
                            title="Make Part Payment"
                          >
                            <TrendingDown className="w-4 h-4" />
                          </button>
                        )}
                        {onUndoLastPartPayment && loan.partPayments.length > 0 && (
                          <button
                            onClick={() => onUndoLastPartPayment?.(loan)}
                            className="p-2 text-white/60 hover:text-yellow-400 transition-colors"
                            title="Undo Last Part Payment"
                          >
                            <RotateCcw className="w-4 h-4" />
                          </button>
                        )}
                        {onDelete && (
                          <button
                            onClick={() => onDelete?.(loan.id)}
                            className="p-2 text-white/60 hover:text-red-400 transition-colors"
                            title="Delete Loan"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        )}
                      </div>
                    </td>
                  )}
                </motion.tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Summary row */}
      <div className="border-t border-white/10 mt-4 pt-4">
        <div className="flex justify-between items-center">
          <span className="text-white/70 font-medium">Total Outstanding Debt:</span>
          <span className="text-xl font-bold text-red-400">
            {formatCurrency(
              activeLoans.reduce((total, loan) => {
                return total + Number(loan.currentPrincipal);
              }, 0)
            )}
          </span>
        </div>
      </div>
    </div>
  );
};

export default LoanTable;

import React from 'react';
import { motion } from 'framer-motion';
import { Loan } from '@/types';
import { calculateLoanDetails, formatCurrency, formatDate } from '@/utils/calculations';
import { Edit, Trash2, DollarSign, TrendingDown, RotateCcw } from 'lucide-react';

interface LoanTableProps {
  loans: Loan[];
  onEdit?: (loan: Loan) => void;
  onDelete?: (loanId: string) => void;
  onPartPayment?: (loan: Loan) => void;
  onUndoLastPartPayment?: (loan: Loan) => void;
  showActions?: boolean;
}

const LoanTable: React.FC<LoanTableProps> = ({
  loans,
  onEdit,
  onDelete,
  onPartPayment,
  onUndoLastPartPayment,
  showActions = false
}) => {
  const activeLoans = loans.filter(loan => loan.isActive);

  if (activeLoans.length === 0) {
    return (
      <div className="glass-card text-center py-12">
        <DollarSign className="w-16 h-16 text-white/30 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-white/70 mb-2">No Active Loans</h3>
        <p className="text-white/50">Add your first loan to get started with tracking</p>
      </div>
    );
  }

  return (
    <div className="glass-card overflow-hidden">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-semibold text-white">Loan Details</h3>
        <span className="text-white/70 text-sm">
          {activeLoans.length} active loan{activeLoans.length !== 1 ? 's' : ''}
        </span>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="border-b border-white/10">
              <th className="text-left py-4 px-6 text-white/90 font-semibold">Loan Name</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Principal Amount</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Interest Rate</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">EMI Amount</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">EMIs Remaining</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Next EMI Date</th>
              <th className="text-right py-4 px-6 text-white/90 font-semibold">Outstanding</th>
              {showActions && (
                <th className="text-center py-4 px-6 text-white/90 font-semibold">Actions</th>
              )}
            </tr>
          </thead>
          <tbody>
            {activeLoans.map((loan, index) => {
              const details = calculateLoanDetails(loan);
              return (
                <motion.tr
                  key={loan.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.05 }}
                  className="border-b border-white/5 hover:bg-white/5 transition-colors"
                >
                  <td className="py-4 px-6">
                    <div>
                      <span className="text-white font-medium">{loan.name}</span>
                      {loan.partPayments.length > 0 && (
                        <div className="text-xs text-green-400 mt-1">
                          {loan.partPayments.length} part payment{loan.partPayments.length !== 1 ? 's' : ''}
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatCurrency(loan.principalAmount)}
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {Number(loan.interestRate).toFixed(2)}%
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatCurrency(details.emiAmount)}
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    <span className={`${details.remainingEmis <= 12 ? 'text-orange-400' : 'text-white/80'}`}>
                      {details.remainingEmis}
                    </span>
                  </td>
                  <td className="py-4 px-6 text-right text-white/80">
                    {formatDate(details.nextEmiDate)}
                  </td>
                  <td className="py-4 px-6 text-right">
                    <span className="text-red-400 font-semibold">
                      {formatCurrency(details.remainingPrincipal)}
                    </span>
                  </td>
                  {showActions && (
                    <td className="py-4 px-6">
                      <div className="flex items-center justify-center space-x-2">
                        {onEdit && (
                          <button
                            onClick={() => onEdit?.(loan)}
                            className="p-2 text-white/60 hover:text-blue-400 transition-colors"
                            title="Edit Loan"
                          >
                            <Edit className="w-4 h-4" />
                          </button>
                        )}
                        {onPartPayment && (
                          <button
                            onClick={() => onPartPayment?.(loan)}
                            className="p-2 text-white/60 hover:text-green-400 transition-colors"
                            title="Make Part Payment"
                          >
                            <TrendingDown className="w-4 h-4" />
                          </button>
                        )}
                        {onUndoLastPartPayment && loan.partPayments.length > 0 && (
                          <button
                            onClick={() => onUndoLastPartPayment?.(loan)}
                            className="p-2 text-white/60 hover:text-yellow-400 transition-colors"
                            title="Undo Last Part Payment"
                          >
                            <RotateCcw className="w-4 h-4" />
                          </button>
                        )}
                        {onDelete && (
                          <button
                            onClick={() => onDelete?.(loan.id)}
                            className="p-2 text-white/60 hover:text-red-400 transition-colors"
                            title="Delete Loan"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        )}
                      </div>
                    </td>
                  )}
                </motion.tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Summary row */}
      <div className="border-t border-white/10 mt-4 pt-4">
        <div className="flex justify-between items-center">
          <span className="text-white/70 font-medium">Total Outstanding Debt:</span>
          <span className="text-xl font-bold text-red-400">
            {formatCurrency(
              activeLoans.reduce((total, loan) => {
                const d = calculateLoanDetails(loan);
                return total + d.remainingPrincipal;
              }, 0)
            )}
          </span>
        </div>
      </div>
    </div>
  );
};

export default LoanTable;

import React from 'react';
import { motion } from 'framer-motion';
import { DashboardData } from '@/types';
import { formatCurrency } from '@/utils/calculations';
import { TrendingUp, TrendingDown, DollarSign, Calendar } from 'lucide-react';

interface SummaryCardsProps {
  dashboardData: DashboardData;
}

const SummaryCards: React.FC<SummaryCardsProps> = ({ dashboardData }) => {
  const cards = [
    {
      title: 'Total Outstanding Debt',
      value: dashboardData.totalOutstandingDebt,
      icon: TrendingDown,
      color: 'text-red-400',
      bgColor: 'bg-red-500/10',
      trend: null,
    },
    {
      title: 'Total Savings',
      value: dashboardData.totalSavings,
      icon: TrendingUp,
      color: 'text-green-400',
      bgColor: 'bg-green-500/10',
      trend: null,
    },
    {
      title: 'Monthly EMI',
      value: dashboardData.monthlyEmi,
      icon: DollarSign,
      color: 'text-orange-400',
      bgColor: 'bg-orange-500/10',
      trend: null,
    },
    {
      title: 'Active Loans',
      value: dashboardData.loans.filter(loan => loan.isActive).length,
      icon: Calendar,
      color: 'text-blue-400',
      bgColor: 'bg-blue-500/10',
      trend: null,
      isCount: true,
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {cards.map((card, index) => (
        <motion.div
          key={card.title}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.1 }}
          className="glass-card hover:scale-105 transition-transform duration-300"
        >
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <p className="text-white/70 text-sm font-medium">{card.title}</p>
              <p className={`text-2xl font-bold ${card.color} mt-2`}>
                {card.isCount ? card.value : formatCurrency(card.value)}
              </p>
            </div>
            <div className={`p-3 rounded-lg ${card.bgColor}`}>
              <card.icon className={`w-6 h-6 ${card.color}`} />
            </div>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default SummaryCards;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { storage } from '@/utils/storage';
import { Download, Upload, Trash2, AlertTriangle, CheckCircle } from 'lucide-react';

const DataManagement: React.FC = () => {
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null);
  const [showClearConfirm, setShowClearConfirm] = useState(false);

  const handleExportData = () => {
    try {
      const exportData = storage.exportData();
      const blob = new Blob([exportData], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `finance-dashboard-backup-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);

      setMessage({ type: 'success', text: 'Data exported successfully!' });
      setTimeout(() => setMessage(null), 3000);
    } catch (error) {
      setMessage({ type: 'error', text: 'Failed to export data.' });
      setTimeout(() => setMessage(null), 3000);
    }
  };

  const handleImportData = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const content = e.target?.result as string;
        const result = storage.importData(content);

        setMessage({ 
          type: result.success ? 'success' : 'error', 
          text: result.message 
        });

        if (result.success) {
          // Refresh the page to load the imported data
          setTimeout(() => window.location.reload(), 1500);
        }

        setTimeout(() => setMessage(null), 3000);
      } catch (error) {
        setMessage({ type: 'error', text: 'Failed to read file.' });
        setTimeout(() => setMessage(null), 3000);
      }
    };
    reader.readAsText(file);

    // Reset file input
    event.target.value = '';
  };

  const handleClearData = () => {
    storage.clearAll();
    setMessage({ type: 'success', text: 'All data cleared successfully!' });
    setShowClearConfirm(false);
    setTimeout(() => {
      setMessage(null);
      window.location.reload();
    }, 1500);
  };

  return (
    <div className="glass-card">
      <div className="flex items-center space-x-3 mb-6">
        <Download className="w-6 h-6 text-blue-400" />
        <div>
          <h3 className="text-xl font-semibold text-white">Data Management</h3>
          <p className="text-white/70 text-sm">Export, import, or clear your financial data</p>
        </div>
      </div>

      {/* Message */}
      {message && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className={`p-3 rounded-lg mb-4 flex items-center space-x-2 ${
            message.type === 'success' 
              ? 'bg-green-500/20 border border-green-500/30 text-green-400' 
              : 'bg-red-500/20 border border-red-500/30 text-red-400'
          }`}
        >
          {message.type === 'success' ? <CheckCircle className="w-4 h-4" /> : <AlertTriangle className="w-4 h-4" />}
          <span>{message.text}</span>
        </motion.div>
      )}

      <div className="space-y-4">
        {/* Export Data */}
        <div className="flex items-center justify-between p-4 bg-white/5 rounded-lg">
          <div>
            <h4 className="font-medium text-white">Export Data</h4>
            <p className="text-white/60 text-sm">Download a backup of all your financial data</p>
          </div>
          <button
            onClick={handleExportData}
            className="glass-button bg-blue-500/20 hover:bg-blue-500/30 flex items-center space-x-2"
          >
            <Download className="w-4 h-4" />
            <span>Export</span>
          </button>
        </div>

        {/* Import Data */}
        <div className="flex items-center justify-between p-4 bg-white/5 rounded-lg">
          <div>
            <h4 className="font-medium text-white">Import Data</h4>
            <p className="text-white/60 text-sm">Restore data from a previously exported backup</p>
          </div>
          <label className="glass-button bg-green-500/20 hover:bg-green-500/30 flex items-center space-x-2 cursor-pointer">
            <Upload className="w-4 h-4" />
            <span>Import</span>
            <input
              type="file"
              accept=".json"
              onChange={handleImportData}
              className="hidden"
            />
          </label>
        </div>

        {/* Clear Data */}
        <div className="flex items-center justify-between p-4 bg-white/5 rounded-lg">
          <div>
            <h4 className="font-medium text-white">Clear All Data</h4>
            <p className="text-white/60 text-sm">Permanently delete all stored financial data</p>
          </div>
          {!showClearConfirm ? (
            <button
              onClick={() => setShowClearConfirm(true)}
              className="glass-button bg-red-500/20 hover:bg-red-500/30 flex items-center space-x-2"
            >
              <Trash2 className="w-4 h-4" />
              <span>Clear</span>
            </button>
          ) : (
            <div className="flex space-x-2">
              <button
                onClick={handleClearData}
                className="px-3 py-2 bg-red-500/30 text-red-400 rounded-lg text-sm hover:bg-red-500/40"
              >
                Confirm
              </button>
              <button
                onClick={() => setShowClearConfirm(false)}
                className="px-3 py-2 bg-gray-500/20 text-white/70 rounded-lg text-sm hover:bg-gray-500/30"
              >
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Storage Information */}
      <div className="mt-6 p-4 bg-yellow-500/10 border border-yellow-500/20 rounded-lg">
        <div className="flex items-start space-x-2">
          <AlertTriangle className="w-5 h-5 text-yellow-400 mt-0.5" />
          <div>
            <h4 className="text-yellow-400 font-medium mb-1">Data Storage Information</h4>
            <ul className="text-white/80 text-sm space-y-1">
              <li>• Data is stored locally in your browser's localStorage</li>
              <li>• Data is browser-specific and won't sync across different browsers</li>
              <li>• Regular exports are recommended to prevent data loss</li>
              <li>• Clearing browser data will delete all your financial information</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DataManagement;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { Loan } from '@/types';
import { calculateEMI } from '@/utils/calculations';
import { X, Calculator } from 'lucide-react';
import { addMonths } from 'date-fns';

interface LoanFormProps {
  loan?: Loan;
  onSubmit: (data: Omit<Loan, 'id'>) => void;
  onCancel: () => void;
  title: string;
}

interface FormData {
  name: string;
  principalAmount: number;
  interestRate: number;
  tenure: number;
  startDate: string;
  customEmi?: number;
  useCustomEmi: boolean;
}

const LoanForm: React.FC<LoanFormProps> = ({ loan, onSubmit, onCancel, title }) => {
  // Check if the existing loan has a custom EMI (different from calculated EMI)
  const hasCustomEmi = loan ? (() => {
    const calculatedEmi = calculateEMI(loan.principalAmount, Number(loan.interestRate), loan.tenure);
    return Math.abs(Number(loan.emiAmount) - calculatedEmi) > 1; // Allow small rounding differences
  })() : false;

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      name: loan?.name || '',
      principalAmount: Number(loan?.principalAmount) || 0,
      interestRate: Number(loan?.interestRate) || 0,
      tenure: Number(loan?.tenure) || 0,
      startDate: loan?.startDate ? loan.startDate.toISOString().split('T')[0] : '',
      customEmi: loan?.useCustomEmi ? Number(loan.customEmi) ?? Number(loan?.emiAmount) || 0 : Number(loan?.emiAmount) || 0,
      useCustomEmi: loan?.useCustomEmi ?? hasCustomEmi
    }
  });

  const watchedValues = watch(['principalAmount', 'interestRate', 'tenure', 'useCustomEmi', 'customEmi']);
  const [principal, rate, tenure, useCustomEmi, customEmi] = watchedValues;

  // Calculate EMI automatically
  const calculatedEmi = principal && rate && tenure
    ? calculateEMI(principal, rate, tenure)
    : 0;

  const handleFormSubmit = (data: FormData) => {
    const startDate = new Date(data.startDate);

    // Use custom EMI if selected, otherwise use calculated EMI
    const emiAmount = data.useCustomEmi && data.customEmi ? Number(data.customEmi) : calculatedEmi;

    const loanData: Omit<Loan, 'id'> = {
      name: data.name,
      principalAmount: Number(data.principalAmount),
      currentPrincipal: loan ? loan.currentPrincipal : Number(data.principalAmount),
      interestRate: Number(data.interestRate),
      emiAmount: Number(emiAmount),
      // persist custom EMI settings
      useCustomEmi: Boolean(data.useCustomEmi),
      customEmi: data.useCustomEmi ? Number(data.customEmi) || Number(emiAmount) : undefined,
      startDate,
      tenure: Number(data.tenure),
      nextEmiDate: addMonths(startDate, 1),
      isActive: true,
      partPayments: loan?.partPayments || [],
      interestRateChanges: loan?.interestRateChanges || []
    };

    onSubmit(loanData);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-2xl max-h-[90vh] overflow-y-auto"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-semibold text-white">{title}</h3>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Loan Name */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Loan Name *
            </label>
            <input
              {...register('name', { required: 'Loan name is required' })}
              className="glass-input w-full"
              placeholder="e.g., Home Loan, Car Loan"
            />
            {errors.name && (
              <p className="text-red-400 text-sm mt-1">{errors.name.message}</p>
            )}
          </div>

          {/* Principal Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Principal Amount (₹) *
            </label>
            <input
              {...register('principalAmount', {
                required: 'Principal amount is required',
                min: { value: 1000, message: 'Minimum amount is ₹1,000' }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="1000000"
            />
            {errors.principalAmount && (
              <p className="text-red-400 text-sm mt-1">{errors.principalAmount.message}</p>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Interest Rate */}
            <div>
              <label className="block text-white/90 text-sm font-medium mb-2">
                Interest Rate (% p.a.) *
              </label>
              <input
                {...register('interestRate', {
                  required: 'Interest rate is required',
                  min: { value: 0.1, message: 'Minimum rate is 0.1%' },
                  max: { value: 50, message: 'Maximum rate is 50%' }
                })}
                type="number"
                step="0.01"
                className="glass-input w-full"
                placeholder="8.5"
              />
              {errors.interestRate && (
                <p className="text-red-400 text-sm mt-1">{errors.interestRate.message}</p>
              )}
            </div>

            {/* Tenure */}
            <div>
              <label className="block text-white/90 text-sm font-medium mb-2">
                Tenure (Months) *
              </label>
              <input
                {...register('tenure', {
                  required: 'Tenure is required',
                  min: { value: 1, message: 'Minimum tenure is 1 month' },
                  max: { value: 360, message: 'Maximum tenure is 360 months' }
                })}
                type="number"
                className="glass-input w-full"
                placeholder="240"
              />
              {errors.tenure && (
                <p className="text-red-400 text-sm mt-1">{errors.tenure.message}</p>
              )}
            </div>
          </div>

          {/* Start Date */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Start Date *
            </label>
            <input
              {...register('startDate', { required: 'Start date is required' })}
              type="date"
              className="glass-input w-full"
            />
            {errors.startDate && (
              <p className="text-red-400 text-sm mt-1">{errors.startDate.message}</p>
            )}
          </div>

          {/* EMI Calculation */}
          <div className="bg-white/5 rounded-lg p-4">
            <div className="flex items-center space-x-2 mb-3">
              <Calculator className="w-5 h-5 text-blue-400" />
              <span className="text-white/90 font-medium">EMI Calculation</span>
            </div>

            {calculatedEmi > 0 && (
              <div className="mb-4">
                <p className="text-white/70 text-sm">Calculated EMI:</p>
                <p className="text-2xl font-bold text-blue-400">
                  ₹{calculatedEmi.toLocaleString('en-IN', { maximumFractionDigits: 0 })}
                </p>
              </div>
            )}

            {/* Show current EMI selection */}
            <div className="mb-4 p-3 bg-white/10 rounded-lg">
              <p className="text-white/70 text-sm">Selected EMI:</p>
              <p className="text-xl font-bold text-green-400">
                ₹{(useCustomEmi ? (Number(customEmi) || calculatedEmi) : calculatedEmi).toLocaleString('en-IN', { maximumFractionDigits: 0 })}
              </p>
            </div>

            <div className="flex items-center space-x-2 mb-3">
              <input
                {...register('useCustomEmi')}
                type="checkbox"
                className="rounded"
              />
              <label className="text-white/90 text-sm">Use custom EMI amount</label>
            </div>

            {useCustomEmi && (
              <div>
                <label className="block text-white/90 text-sm font-medium mb-2">
                  Custom EMI Amount (₹)
                </label>
                <input
                  {...register('customEmi', {
                    required: useCustomEmi ? 'Custom EMI is required' : false,
                    min: { value: 100, message: 'Minimum EMI is ₹100' }
                  })}
                  type="number"
                  className="glass-input w-full"
                  placeholder={calculatedEmi.toString()}
                />
                {errors.customEmi && (
                  <p className="text-red-400 text-sm mt-1">{errors.customEmi.message}</p>
                )}
              </div>
            )}
          </div>

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-blue-500/20 hover:bg-blue-500/30"
            >
              {loan ? 'Update Loan' : 'Add Loan'}
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default LoanForm;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { Loan } from '@/types';
import { calculateEMI } from '@/utils/calculations';
import { X, Calculator } from 'lucide-react';
import { addMonths } from 'date-fns';

interface LoanFormProps {
  loan?: Loan;
  onSubmit: (data: Omit<Loan, 'id'>) => void;
  onCancel: () => void;
  title: string;
}

interface FormData {
  name: string;
  principalAmount: number;
  interestRate: number;
  tenure: number;
  startDate: string;
  customEmi?: number;
  useCustomEmi: boolean;
}

const LoanForm: React.FC<LoanFormProps> = ({ loan, onSubmit, onCancel, title }) => {
  // Check if the existing loan has a custom EMI (different from calculated EMI)
  const hasCustomEmi = loan ? (() => {
    const calculatedEmi = calculateEMI(loan.principalAmount, Number(loan.interestRate), loan.tenure);
    return Math.abs(Number(loan.emiAmount) - calculatedEmi) > 1; // Allow small rounding differences
  })() : false;

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      name: loan?.name || '',
      principalAmount: Number(loan?.principalAmount) || 0,
      interestRate: Number(loan?.interestRate) || 0,
      tenure: Number(loan?.tenure) || 0,
      startDate: loan?.startDate ? loan.startDate.toISOString().split('T')[0] : '',
      customEmi: loan?.useCustomEmi
        ? (Number(loan?.customEmi ?? loan?.emiAmount) || 0)
        : (Number(loan?.emiAmount) || 0),
      useCustomEmi: loan?.useCustomEmi ?? hasCustomEmi
    }
  });

  const watchedValues = watch(['principalAmount', 'interestRate', 'tenure', 'useCustomEmi', 'customEmi']);
  const [principal, rate, tenure, useCustomEmi, customEmi] = watchedValues;

  // Calculate EMI automatically
  const calculatedEmi = principal && rate && tenure
    ? calculateEMI(principal, rate, tenure)
    : 0;

  const handleFormSubmit = (data: FormData) => {
    const startDate = new Date(data.startDate);

    // Use custom EMI if selected, otherwise use calculated EMI
    const emiAmount = data.useCustomEmi && data.customEmi ? Number(data.customEmi) : calculatedEmi;

    const loanData: Omit<Loan, 'id'> = {
      name: data.name,
      principalAmount: Number(data.principalAmount),
      currentPrincipal: loan ? loan.currentPrincipal : Number(data.principalAmount),
      interestRate: Number(data.interestRate),
      emiAmount: Number(emiAmount),
      // persist custom EMI settings
      useCustomEmi: Boolean(data.useCustomEmi),
      customEmi: data.useCustomEmi ? Number(data.customEmi) || Number(emiAmount) : undefined,
      startDate,
      tenure: Number(data.tenure),
      nextEmiDate: addMonths(startDate, 1),
      isActive: true,
      partPayments: loan?.partPayments || [],
      interestRateChanges: loan?.interestRateChanges || []
    };

    onSubmit(loanData);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-2xl max-h-[90vh] overflow-y-auto"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-semibold text-white">{title}</h3>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Loan Name */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Loan Name *
            </label>
            <input
              {...register('name', { required: 'Loan name is required' })}
              className="glass-input w-full"
              placeholder="e.g., Home Loan, Car Loan"
            />
            {errors.name && (
              <p className="text-red-400 text-sm mt-1">{errors.name.message}</p>
            )}
          </div>

          {/* Principal Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Principal Amount (₹) *
            </label>
            <input
              {...register('principalAmount', {
                required: 'Principal amount is required',
                min: { value: 1000, message: 'Minimum amount is ₹1,000' }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="1000000"
            />
            {errors.principalAmount && (
              <p className="text-red-400 text-sm mt-1">{errors.principalAmount.message}</p>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Interest Rate */}
            <div>
              <label className="block text-white/90 text-sm font-medium mb-2">
                Interest Rate (% p.a.) *
              </label>
              <input
                {...register('interestRate', {
                  required: 'Interest rate is required',
                  min: { value: 0.1, message: 'Minimum rate is 0.1%' },
                  max: { value: 50, message: 'Maximum rate is 50%' }
                })}
                type="number"
                step="0.01"
                className="glass-input w-full"
                placeholder="8.5"
              />
              {errors.interestRate && (
                <p className="text-red-400 text-sm mt-1">{errors.interestRate.message}</p>
              )}
            </div>

            {/* Tenure */}
            <div>
              <label className="block text-white/90 text-sm font-medium mb-2">
                Tenure (Months) *
              </label>
              <input
                {...register('tenure', {
                  required: 'Tenure is required',
                  min: { value: 1, message: 'Minimum tenure is 1 month' },
                  max: { value: 360, message: 'Maximum tenure is 360 months' }
                })}
                type="number"
                className="glass-input w-full"
                placeholder="240"
              />
              {errors.tenure && (
                <p className="text-red-400 text-sm mt-1">{errors.tenure.message}</p>
              )}
            </div>
          </div>

          {/* Start Date */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Start Date *
            </label>
            <input
              {...register('startDate', { required: 'Start date is required' })}
              type="date"
              className="glass-input w-full"
            />
            {errors.startDate && (
              <p className="text-red-400 text-sm mt-1">{errors.startDate.message}</p>
            )}
          </div>

          {/* EMI Calculation */}
          <div className="bg-white/5 rounded-lg p-4">
            <div className="flex items-center space-x-2 mb-3">
              <Calculator className="w-5 h-5 text-blue-400" />
              <span className="text-white/90 font-medium">EMI Calculation</span>
            </div>

            {calculatedEmi > 0 && (
              <div className="mb-4">
                <p className="text-white/70 text-sm">Calculated EMI:</p>
                <p className="text-2xl font-bold text-blue-400">
                  ₹{calculatedEmi.toLocaleString('en-IN', { maximumFractionDigits: 0 })}
                </p>
              </div>
            )}

            {/* Show current EMI selection */}
            <div className="mb-4 p-3 bg-white/10 rounded-lg">
              <p className="text-white/70 text-sm">Selected EMI:</p>
              <p className="text-xl font-bold text-green-400">
                ₹{(useCustomEmi ? (Number(customEmi) || calculatedEmi) : calculatedEmi).toLocaleString('en-IN', { maximumFractionDigits: 0 })}
              </p>
            </div>

            <div className="flex items-center space-x-2 mb-3">
              <input
                {...register('useCustomEmi')}
                type="checkbox"
                className="rounded"
              />
              <label className="text-white/90 text-sm">Use custom EMI amount</label>
            </div>

            {useCustomEmi && (
              <div>
                <label className="block text-white/90 text-sm font-medium mb-2">
                  Custom EMI Amount (₹)
                </label>
                <input
                  {...register('customEmi', {
                    required: useCustomEmi ? 'Custom EMI is required' : false,
                    min: { value: 100, message: 'Minimum EMI is ₹100' }
                  })}
                  type="number"
                  className="glass-input w-full"
                  placeholder={calculatedEmi.toString()}
                />
                {errors.customEmi && (
                  <p className="text-red-400 text-sm mt-1">{errors.customEmi.message}</p>
                )}
              </div>
            )}
          </div>

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-blue-500/20 hover:bg-blue-500/30"
            >
              {loan ? 'Update Loan' : 'Add Loan'}
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default LoanForm;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useLoans } from '@/hooks/useFinance';
import { Loan, PartPayment } from '@/types';
import LoanForm from './LoanForm';
import LoanTable from '../Dashboard/LoanTable';
import PartPaymentForm from './PartPaymentForm';
import { Plus, Calculator, CreditCard } from 'lucide-react';
import { v4 as uuidv4 } from 'uuid';

const LoanManagement: React.FC = () => {
  const { loans, addLoan, updateLoan, deleteLoan, addPartPayment } = useLoans();
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingLoan, setEditingLoan] = useState<Loan | null>(null);
  const [showPartPaymentForm, setShowPartPaymentForm] = useState<string | null>(null);

  const handleAddLoan = (loanData: Omit<Loan, 'id'>) => {
    const newLoan: Loan = {
      ...loanData,
      id: uuidv4(),
    };
    addLoan(newLoan);
    setShowAddForm(false);
  };

  const handleEditLoan = (loanData: Omit<Loan, 'id'>) => {
    if (editingLoan) {
      // Preserve existing loan properties that shouldn't be reset
      const updatedLoanData = {
        ...loanData,
        currentPrincipal: editingLoan.currentPrincipal, // Keep current principal (affected by part payments)
        partPayments: editingLoan.partPayments, // Keep existing part payments
        interestRateChanges: editingLoan.interestRateChanges, // Keep rate change history
        lastEmiDate: editingLoan.lastEmiDate, // Keep last EMI date if it exists
      };

      updateLoan(editingLoan.id, updatedLoanData);
      setEditingLoan(null);
    }
  };

  const handleDeleteLoan = (loanId: string) => {
    if (confirm('Are you sure you want to delete this loan?')) {
      deleteLoan(loanId);
    }
  };

  const handlePartPayment = (loanId: string, partPaymentData: Omit<PartPayment, 'id'>) => {
    const partPayment: PartPayment = {
      ...partPaymentData,
      id: uuidv4(),
    };
    addPartPayment(loanId, partPayment);
    setShowPartPaymentForm(null);
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-white">Loan Management</h2>
          <p className="text-white/70">Add, edit, and manage your loans</p>
        </div>
        <motion.button
          onClick={() => setShowAddForm(true)}
          className="glass-button flex items-center space-x-2"
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <Plus className="w-5 h-5" />
          <span>Add New Loan</span>
        </motion.button>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-card text-center"
        >
          <CreditCard className="w-8 h-8 text-blue-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Total Loans</p>
          <p className="text-2xl font-bold text-white">{loans.length}</p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="glass-card text-center"
        >
          <Calculator className="w-8 h-8 text-green-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Active Loans</p>
          <p className="text-2xl font-bold text-white">
            {loans.filter(loan => loan.isActive).length}
          </p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="glass-card text-center"
        >
          <Plus className="w-8 h-8 text-purple-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Part Payments</p>
          <p className="text-2xl font-bold text-white">
            {loans.reduce((total, loan) => total + loan.partPayments.length, 0)}
          </p>
        </motion.div>
      </div>

      {/* Loan Table */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
      >
        <LoanTable
          loans={loans}
          onEdit={setEditingLoan}
          onDelete={handleDeleteLoan}
          onPartPayment={(loan) => setShowPartPaymentForm(loan.id)}
          showActions={true}
        />
      </motion.div>

      {/* Part Payment Actions */}
      {loans.filter(loan => loan.isActive).length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="glass-card"
        >
          <h3 className="text-lg font-semibold text-white mb-4">Quick Actions</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {loans.filter(loan => loan.isActive).map(loan => (
              <button
                key={loan.id}
                onClick={() => setShowPartPaymentForm(loan.id)}
                className="p-4 bg-white/5 rounded-lg hover:bg-white/10 transition-colors text-left"
              >
                <h4 className="font-medium text-white">{loan.name}</h4>
                <p className="text-white/70 text-sm">Make part payment</p>
              </button>
            ))}
          </div>
        </motion.div>
      )}

      {/* Add Loan Modal */}
      {showAddForm && (
        <LoanForm
          onSubmit={handleAddLoan}
          onCancel={() => setShowAddForm(false)}
          title="Add New Loan"
        />
      )}

      {/* Edit Loan Modal */}
      {editingLoan && (
        <LoanForm
          loan={editingLoan}
          onSubmit={handleEditLoan}
          onCancel={() => setEditingLoan(null)}
          title="Edit Loan"
        />
      )}

      {/* Part Payment Modal */}
      {showPartPaymentForm && (
        <PartPaymentForm
          loanId={showPartPaymentForm}
          loan={loans.find(l => l.id === showPartPaymentForm)!}
          onSubmit={(data) => handlePartPayment(showPartPaymentForm, data)}
          onCancel={() => setShowPartPaymentForm(null)}
        />
      )}
    </div>
  );
};

export default LoanManagement;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useLoans } from '@/hooks/useFinance';
import { Loan, PartPayment } from '@/types';
import LoanForm from './LoanForm';
import LoanTable from '../Dashboard/LoanTable';
import PartPaymentForm from './PartPaymentForm';
import { Plus, Calculator, CreditCard } from 'lucide-react';
import { v4 as uuidv4 } from 'uuid';

const LoanManagement: React.FC = () => {
  const { loans, addLoan, updateLoan, deleteLoan, addPartPayment, removeLastPartPayment } = useLoans();
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingLoan, setEditingLoan] = useState<Loan | null>(null);
  const [showPartPaymentForm, setShowPartPaymentForm] = useState<string | null>(null);

  const handleAddLoan = (loanData: Omit<Loan, 'id'>) => {
    const newLoan: Loan = {
      ...loanData,
      id: uuidv4(),
    };
    addLoan(newLoan);
    setShowAddForm(false);
  };

  const handleEditLoan = (loanData: Omit<Loan, 'id'>) => {
    if (editingLoan) {
      // Preserve existing loan properties that shouldn't be reset
      const updatedLoanData = {
        ...loanData,
        currentPrincipal: editingLoan.currentPrincipal, // Keep current principal (affected by part payments)
        partPayments: editingLoan.partPayments, // Keep existing part payments
        interestRateChanges: editingLoan.interestRateChanges, // Keep rate change history
        lastEmiDate: editingLoan.lastEmiDate, // Keep last EMI date if it exists
      };

      updateLoan(editingLoan.id, updatedLoanData);
      setEditingLoan(null);
    }
  };

  const handleDeleteLoan = (loanId: string) => {
    if (confirm('Are you sure you want to delete this loan?')) {
      deleteLoan(loanId);
    }
  };

  const handlePartPayment = (loanId: string, partPaymentData: Omit<PartPayment, 'id'>) => {
    const partPayment: PartPayment = {
      ...partPaymentData,
      id: uuidv4(),
    };
    addPartPayment(loanId, partPayment);
    setShowPartPaymentForm(null);
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-white">Loan Management</h2>
          <p className="text-white/70">Add, edit, and manage your loans</p>
        </div>
        <motion.button
          onClick={() => setShowAddForm(true)}
          className="glass-button flex items-center space-x-2"
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <Plus className="w-5 h-5" />
          <span>Add New Loan</span>
        </motion.button>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-card text-center"
        >
          <CreditCard className="w-8 h-8 text-blue-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Total Loans</p>
          <p className="text-2xl font-bold text-white">{loans.length}</p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="glass-card text-center"
        >
          <Calculator className="w-8 h-8 text-green-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Active Loans</p>
          <p className="text-2xl font-bold text-white">
            {loans.filter(loan => loan.isActive).length}
          </p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="glass-card text-center"
        >
          <Plus className="w-8 h-8 text-purple-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Part Payments</p>
          <p className="text-2xl font-bold text-white">
            {loans.reduce((total, loan) => total + loan.partPayments.length, 0)}
          </p>
        </motion.div>
      </div>

      {/* Loan Table */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
      >
        <LoanTable
          loans={loans}
          onEdit={setEditingLoan}
          onDelete={handleDeleteLoan}
          onPartPayment={(loan) => setShowPartPaymentForm(loan.id)}
          onUndoLastPartPayment={(loan) => {
            if (confirm('Undo the last part payment for this loan?')) {
              removeLastPartPayment(loan.id);
            }
          }}
          showActions={true}
        />
      </motion.div>

      {/* Part Payment Actions */}
      {loans.filter(loan => loan.isActive).length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="glass-card"
        >
          <h3 className="text-lg font-semibold text-white mb-4">Quick Actions</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {loans.filter(loan => loan.isActive).map(loan => (
              <button
                key={loan.id}
                onClick={() => setShowPartPaymentForm(loan.id)}
                className="p-4 bg-white/5 rounded-lg hover:bg-white/10 transition-colors text-left"
              >
                <h4 className="font-medium text-white">{loan.name}</h4>
                <p className="text-white/70 text-sm">Make part payment</p>
              </button>
            ))}
          </div>
        </motion.div>
      )}

      {/* Add Loan Modal */}
      {showAddForm && (
        <LoanForm
          onSubmit={handleAddLoan}
          onCancel={() => setShowAddForm(false)}
          title="Add New Loan"
        />
      )}

      {/* Edit Loan Modal */}
      {editingLoan && (
        <LoanForm
          loan={editingLoan}
          onSubmit={handleEditLoan}
          onCancel={() => setEditingLoan(null)}
          title="Edit Loan"
        />
      )}

      {/* Part Payment Modal */}
      {showPartPaymentForm && (
        <PartPaymentForm
          loanId={showPartPaymentForm}
          loan={loans.find(l => l.id === showPartPaymentForm)!}
          onSubmit={(data) => handlePartPayment(showPartPaymentForm, data)}
          onCancel={() => setShowPartPaymentForm(null)}
        />
      )}
    </div>
  );
};

export default LoanManagement;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { Loan, PartPayment } from '@/types';
import { calculateLoanDetails, calculatePartPaymentSavings, formatCurrency } from '@/utils/calculations';
import { X, TrendingDown, Calculator } from 'lucide-react';

interface PartPaymentFormProps {
  loanId: string;
  loan: Loan;
  onSubmit: (data: Omit<PartPayment, 'id'>) => void;
  onCancel: () => void;
}

interface FormData {
  amount: number;
  date: string;
  description: string;
}

const PartPaymentForm: React.FC<PartPaymentFormProps> = ({
  loan,
  onSubmit,
  onCancel
}) => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      amount: 0,
      date: new Date().toISOString().split('T')[0],
      description: ''
    }
  });

  const watchedAmount = watch('amount');
  const loanDetails = calculateLoanDetails(loan);
  const potentialSavings = watchedAmount > 0
    ? calculatePartPaymentSavings(loan, watchedAmount)
    : 0;

  const handleFormSubmit = (data: FormData) => {
    const partPayment: Omit<PartPayment, 'id'> = {
      amount: data.amount,
      date: new Date(data.date),
      description: data.description || `Part payment of ${formatCurrency(data.amount)}`
    };

    onSubmit(partPayment);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-2xl"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h3 className="text-xl font-semibold text-white">Make Part Payment</h3>
            <p className="text-white/70 text-sm">{loan.name}</p>
          </div>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Loan Summary */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">Outstanding Principal</p>
            <p className="text-lg font-semibold text-red-400">
              {formatCurrency(loanDetails.remainingPrincipal)}
            </p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">Current EMI</p>
            <p className="text-lg font-semibold text-blue-400">
              {formatCurrency(loanDetails.emiAmount)}
            </p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">EMIs Remaining</p>
            <p className="text-lg font-semibold text-orange-400">
              {loanDetails.remainingEmis}
            </p>
          </div>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Part Payment Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Part Payment Amount (₹) *
            </label>
            <input
              {...register('amount', {
                required: 'Part payment amount is required',
                min: { value: 1000, message: 'Minimum part payment is ₹1,000' },
                max: {
                  value: loanDetails.remainingPrincipal,
                  message: `Cannot exceed outstanding principal of ${formatCurrency(loanDetails.remainingPrincipal)}`
                }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="50000"
            />
            {errors.amount && (
              <p className="text-red-400 text-sm mt-1">{errors.amount.message}</p>
            )}
          </div>

          {/* Payment Date */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Payment Date *
            </label>
            <input
              {...register('date', { required: 'Payment date is required' })}
              type="date"
              className="glass-input w-full"
            />
            {errors.date && (
              <p className="text-red-400 text-sm mt-1">{errors.date.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Description (Optional)
            </label>
            <textarea
              {...register('description')}
              className="glass-input w-full h-20 resize-none"
              placeholder="Reason for part payment..."
            />
          </div>

          {/* Impact Analysis */}
          {watchedAmount > 0 && (
            <div className="bg-green-500/10 border border-green-500/20 rounded-lg p-4">
              <div className="flex items-center space-x-2 mb-3">
                <Calculator className="w-5 h-5 text-green-400" />
                <span className="text-green-400 font-medium">Impact Analysis</span>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-white/70 text-sm">Potential Interest Savings</p>
                  <p className="text-xl font-bold text-green-400">
                    {formatCurrency(potentialSavings)}
                  </p>
                </div>
                <div>
                  <p className="text-white/70 text-sm">New Outstanding Balance</p>
                  <p className="text-xl font-bold text-blue-400">
                    {formatCurrency(loanDetails.remainingPrincipal - watchedAmount)}
                  </p>
                </div>
              </div>

              <div className="mt-3 p-3 bg-white/5 rounded-lg">
                <p className="text-white/80 text-sm">
                   This part payment could save you <strong>{formatCurrency(potentialSavings)}</strong> in
                  interest over the loan tenure and help you become debt-free faster!
                </p>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-green-500/20 hover:bg-green-500/30"
              disabled={!watchedAmount || watchedAmount < 1000}
            >
              <TrendingDown className="w-4 h-4 mr-2" />
              Make Part Payment
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default PartPaymentForm;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { Loan, PartPayment } from '@/types';
import { calculateLoanDetails, calculatePartPaymentSavings, formatCurrency } from '@/utils/calculations';
import { X, TrendingDown, Calculator } from 'lucide-react';

interface PartPaymentFormProps {
  loanId: string;
  loan: Loan;
  onSubmit: (data: Omit<PartPayment, 'id'>) => void;
  onCancel: () => void;
}

interface FormData {
  amount: number;
  date: string;
  description: string;
}

const PartPaymentForm: React.FC<PartPaymentFormProps> = ({
  loan,
  onSubmit,
  onCancel
}) => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      amount: 0,
      date: new Date().toISOString().split('T')[0],
      description: ''
    }
  });

  const watchedAmount = watch('amount');
  const loanDetails = calculateLoanDetails(loan);
  const potentialSavings = watchedAmount > 0
    ? calculatePartPaymentSavings(loan, watchedAmount)
    : 0;

  const handleFormSubmit = (data: FormData) => {
    const partPayment: Omit<PartPayment, 'id'> = {
      amount: Number(data.amount),
      date: new Date(data.date),
      description: data.description || `Part payment of ${formatCurrency(Number(data.amount))}`
    };

    onSubmit(partPayment);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-2xl"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h3 className="text-xl font-semibold text-white">Make Part Payment</h3>
            <p className="text-white/70 text-sm">{loan.name}</p>
          </div>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Loan Summary */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">Outstanding Principal</p>
            <p className="text-lg font-semibold text-red-400">
              {formatCurrency(loanDetails.remainingPrincipal)}
            </p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">Current EMI</p>
            <p className="text-lg font-semibold text-blue-400">
              {formatCurrency(loanDetails.emiAmount)}
            </p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <p className="text-white/70 text-sm">EMIs Remaining</p>
            <p className="text-lg font-semibold text-orange-400">
              {loanDetails.remainingEmis}
            </p>
          </div>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Part Payment Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Part Payment Amount (₹) *
            </label>
            <input
              {...register('amount', {
                required: 'Part payment amount is required',
                min: { value: 1000, message: 'Minimum part payment is ₹1,000' },
                max: {
                  value: loanDetails.remainingPrincipal,
                  message: `Cannot exceed outstanding principal of ${formatCurrency(loanDetails.remainingPrincipal)}`
                }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="50000"
            />
            {errors.amount && (
              <p className="text-red-400 text-sm mt-1">{errors.amount.message}</p>
            )}
          </div>

          {/* Payment Date */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Payment Date *
            </label>
            <input
              {...register('date', { required: 'Payment date is required' })}
              type="date"
              className="glass-input w-full"
            />
            {errors.date && (
              <p className="text-red-400 text-sm mt-1">{errors.date.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Description (Optional)
            </label>
            <textarea
              {...register('description')}
              className="glass-input w-full h-20 resize-none"
              placeholder="Reason for part payment..."
            />
          </div>

          {/* Impact Analysis */}
          {watchedAmount > 0 && (
            <div className="bg-green-500/10 border border-green-500/20 rounded-lg p-4">
              <div className="flex items-center space-x-2 mb-3">
                <Calculator className="w-5 h-5 text-green-400" />
                <span className="text-green-400 font-medium">Impact Analysis</span>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="text-white/70 text-sm">Potential Interest Savings</p>
                  <p className="text-xl font-bold text-green-400">
                    {formatCurrency(potentialSavings)}
                  </p>
                </div>
                <div>
                  <p className="text-white/70 text-sm">New Outstanding Balance</p>
                  <p className="text-xl font-bold text-blue-400">
                    {formatCurrency(loanDetails.remainingPrincipal - watchedAmount)}
                  </p>
                </div>
              </div>

              <div className="mt-3 p-3 bg-white/5 rounded-lg">
                <p className="text-white/80 text-sm">
                   This part payment could save you <strong>{formatCurrency(potentialSavings)}</strong> in
                  interest over the loan tenure and help you become debt-free faster!
                </p>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-green-500/20 hover:bg-green-500/30"
              disabled={!watchedAmount || watchedAmount < 1000}
            >
              <TrendingDown className="w-4 h-4 mr-2" />
              Make Part Payment
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default PartPaymentForm;

import React from 'react';
import { motion } from 'framer-motion';

interface Tab {
  id: string;
  label: string;
  component: React.ComponentType;
}

interface NavigationProps {
  tabs: Tab[];
  activeTab: string;
  onTabChange: (tabId: string) => void;
}

const Navigation: React.FC<NavigationProps> = ({ tabs, activeTab, onTabChange }) => {
  return (
    <nav className="glass border-b border-white/10">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex space-x-8 overflow-x-auto">
          {tabs.map((tab) => (
            <motion.button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              className={`nav-tab whitespace-nowrap ${
                activeTab === tab.id ? 'active' : ''
              }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {tab.label}
            </motion.button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useSavings } from '@/hooks/useFinance';
import { SavingsAccount, SavingsCategory } from '@/types';
import SavingsForm from './SavingsForm';
import SavingsCard from './SavingsCard';
import { Plus, PiggyBank, TrendingUp, Target } from 'lucide-react';
import { formatCurrency } from '@/utils/calculations';
import { v4 as uuidv4 } from 'uuid';

const Savings: React.FC = () => {
  const { savings, totalSavings, addSavingsAccount, updateSavingsAccount, deleteSavingsAccount } = useSavings();
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingAccount, setEditingAccount] = useState<SavingsAccount | null>(null);

  const handleAddSavings = (accountData: Omit<SavingsAccount, 'id' | 'dateAdded' | 'lastUpdated'>) => {
    const newAccount: SavingsAccount = {
      ...accountData,
      id: uuidv4(),
      dateAdded: new Date(),
      lastUpdated: new Date(),
    };
    addSavingsAccount(newAccount);
    setShowAddForm(false);
  };

  const handleEditSavings = (accountData: Omit<SavingsAccount, 'id' | 'dateAdded' | 'lastUpdated'>) => {
    if (editingAccount) {
      updateSavingsAccount(editingAccount.id, {
        ...accountData,
        lastUpdated: new Date(),
      });
      setEditingAccount(null);
    }
  };

  const handleDeleteSavings = (accountId: string) => {
    if (confirm('Are you sure you want to delete this savings account?')) {
      deleteSavingsAccount(accountId);
    }
  };

  // Group savings by category
  const savingsByCategory = savings.reduce((acc, account) => {
    if (!acc[account.category]) {
      acc[account.category] = [];
    }
    acc[account.category].push(account);
    return acc;
  }, {} as Record<SavingsCategory, SavingsAccount[]>);

  const categoryTotals = Object.entries(savingsByCategory).map(([category, accounts]) => ({
    category: category as SavingsCategory,
    total: accounts.reduce((sum, account) => sum + account.amount, 0),
    count: accounts.length
  }));

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-white">Savings Management</h2>
          <p className="text-white/70">Track and manage your savings across different categories</p>
        </div>
        <motion.button
          onClick={() => setShowAddForm(true)}
          className="glass-button flex items-center space-x-2"
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <Plus className="w-5 h-5" />
          <span>Add Savings Account</span>
        </motion.button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-card text-center"
        >
          <PiggyBank className="w-8 h-8 text-green-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Total Savings</p>
          <p className="text-3xl font-bold text-green-400">{formatCurrency(totalSavings)}</p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="glass-card text-center"
        >
          <Target className="w-8 h-8 text-blue-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Savings Accounts</p>
          <p className="text-3xl font-bold text-blue-400">{savings.length}</p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="glass-card text-center"
        >
          <TrendingUp className="w-8 h-8 text-purple-400 mx-auto mb-2" />
          <p className="text-white/70 text-sm">Categories</p>
          <p className="text-3xl font-bold text-purple-400">{categoryTotals.length}</p>
        </motion.div>
      </div>

      {/* Category Overview */}
      {categoryTotals.length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="glass-card"
        >
          <h3 className="text-xl font-semibold text-white mb-6">Savings by Category</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {categoryTotals.map(({ category, total, count }) => (
              <div key={category} className="bg-white/5 rounded-lg p-4">
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-medium text-white">{category}</h4>
                  <span className="text-white/60 text-xs">{count} account{count !== 1 ? 's' : ''}</span>
                </div>
                <p className="text-lg font-bold text-green-400">{formatCurrency(total)}</p>
                <div className="w-full bg-white/10 rounded-full h-2 mt-2">
                  <div 
                    className="bg-green-400 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${(total / totalSavings) * 100}%` }}
                  />
                </div>
                <p className="text-white/60 text-xs mt-1">
                  {((total / totalSavings) * 100).toFixed(1)}% of total savings
                </p>
              </div>
            ))}
          </div>
        </motion.div>
      )}

      {/* Savings Accounts */}
      {savings.length > 0 ? (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="space-y-6"
        >
          <h3 className="text-xl font-semibold text-white">Your Savings Accounts</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {savings.map((account, index) => (
              <motion.div
                key={account.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.1 * index }}
              >
                <SavingsCard
                  account={account}
                  onEdit={() => setEditingAccount(account)}
                  onDelete={() => handleDeleteSavings(account.id)}
                />
              </motion.div>
            ))}
          </div>
        </motion.div>
      ) : (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="glass-card text-center py-12"
        >
          <PiggyBank className="w-16 h-16 text-white/30 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-white/70 mb-2">No Savings Accounts</h3>
          <p className="text-white/50 mb-6">Start tracking your savings by adding your first account</p>
          <button
            onClick={() => setShowAddForm(true)}
            className="glass-button"
          >
            Add Your First Savings Account
          </button>
        </motion.div>
      )}

      {/* Add Savings Modal */}
      {showAddForm && (
        <SavingsForm
          onSubmit={handleAddSavings}
          onCancel={() => setShowAddForm(false)}
          title="Add Savings Account"
        />
      )}

      {/* Edit Savings Modal */}
      {editingAccount && (
        <SavingsForm
          account={editingAccount}
          onSubmit={handleEditSavings}
          onCancel={() => setEditingAccount(null)}
          title="Edit Savings Account"
        />
      )}
    </div>
  );
};

export default Savings;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { SavingsAccount, SavingsCategory } from '@/types';
import { X } from 'lucide-react';

interface SavingsFormProps {
  account?: SavingsAccount;
  onSubmit: (data: Omit<SavingsAccount, 'id' | 'dateAdded' | 'lastUpdated'>) => void;
  onCancel: () => void;
  title: string;
}

interface FormData {
  name: string;
  category: SavingsCategory;
  amount: number;
  description: string;
}

const savingsCategories: SavingsCategory[] = [
  'Emergency Fund',
  'Fixed Deposit',
  'Mutual Funds',
  'Savings Account',
  'PPF',
  'Other'
];

const SavingsForm: React.FC<SavingsFormProps> = ({ account, onSubmit, onCancel, title }) => {
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      name: account?.name || '',
      category: account?.category || 'Savings Account',
      amount: account?.amount || 0,
      description: account?.description || ''
    }
  });

  const handleFormSubmit = (data: FormData) => {
    onSubmit(data);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-lg"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-semibold text-white">{title}</h3>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Account Name */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Account Name *
            </label>
            <input
              {...register('name', { required: 'Account name is required' })}
              className="glass-input w-full"
              placeholder="e.g., Emergency Fund, SBI FD"
            />
            {errors.name && (
              <p className="text-red-400 text-sm mt-1">{errors.name.message}</p>
            )}
          </div>

          {/* Category */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Category *
            </label>
            <select
              {...register('category', { required: 'Category is required' })}
              className="glass-input w-full"
            >
              {savingsCategories.map(category => (
                <option key={category} value={category} className="bg-slate-800">
                  {category}
                </option>
              ))}
            </select>
            {errors.category && (
              <p className="text-red-400 text-sm mt-1">{errors.category.message}</p>
            )}
          </div>

          {/* Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Amount (₹) *
            </label>
            <input
              {...register('amount', {
                required: 'Amount is required',
                min: { value: 0, message: 'Amount must be positive' }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="50000"
            />
            {errors.amount && (
              <p className="text-red-400 text-sm mt-1">{errors.amount.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Description (Optional)
            </label>
            <textarea
              {...register('description')}
              className="glass-input w-full h-20 resize-none"
              placeholder="Additional details about this savings account..."
            />
          </div>

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-green-500/20 hover:bg-green-500/30"
            >
              {account ? 'Update Account' : 'Add Account'}
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default SavingsForm;

import React from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { SavingsAccount, SavingsCategory } from '@/types';
import { X } from 'lucide-react';

interface SavingsFormProps {
  account?: SavingsAccount;
  onSubmit: (data: Omit<SavingsAccount, 'id' | 'dateAdded' | 'lastUpdated'>) => void;
  onCancel: () => void;
  title: string;
}

interface FormData {
  name: string;
  category: SavingsCategory;
  amount: number;
  description: string;
}

const savingsCategories: SavingsCategory[] = [
  'Emergency Fund',
  'Fixed Deposit',
  'Mutual Funds',
  'Savings Account',
  'PPF',
  'Other'
];

const SavingsForm: React.FC<SavingsFormProps> = ({ account, onSubmit, onCancel, title }) => {
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm<FormData>({
    defaultValues: {
      name: account?.name || '',
      category: account?.category || 'Savings Account',
      amount: account?.amount || 0,
      description: account?.description || ''
    }
  });

  const handleFormSubmit = (data: FormData) => {
    const formattedData = {
      ...data,
      amount: Number(data.amount)
    };
    onSubmit(formattedData);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className="glass-card w-full max-w-lg"
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-semibold text-white">{title}</h3>
          <button
            onClick={onCancel}
            className="p-2 text-white/60 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
          {/* Account Name */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Account Name *
            </label>
            <input
              {...register('name', { required: 'Account name is required' })}
              className="glass-input w-full"
              placeholder="e.g., Emergency Fund, SBI FD"
            />
            {errors.name && (
              <p className="text-red-400 text-sm mt-1">{errors.name.message}</p>
            )}
          </div>

          {/* Category */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Category *
            </label>
            <select
              {...register('category', { required: 'Category is required' })}
              className="glass-input w-full"
            >
              {savingsCategories.map(category => (
                <option key={category} value={category} className="bg-slate-800">
                  {category}
                </option>
              ))}
            </select>
            {errors.category && (
              <p className="text-red-400 text-sm mt-1">{errors.category.message}</p>
            )}
          </div>

          {/* Amount */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Amount (₹) *
            </label>
            <input
              {...register('amount', {
                required: 'Amount is required',
                min: { value: 0, message: 'Amount must be positive' }
              })}
              type="number"
              className="glass-input w-full"
              placeholder="50000"
            />
            {errors.amount && (
              <p className="text-red-400 text-sm mt-1">{errors.amount.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <label className="block text-white/90 text-sm font-medium mb-2">
              Description (Optional)
            </label>
            <textarea
              {...register('description')}
              className="glass-input w-full h-20 resize-none"
              placeholder="Additional details about this savings account..."
            />
          </div>

          {/* Form Actions */}
          <div className="flex space-x-4 pt-4">
            <button
              type="submit"
              className="glass-button flex-1 bg-green-500/20 hover:bg-green-500/30"
            >
              {account ? 'Update Account' : 'Add Account'}
            </button>
            <button
              type="button"
              onClick={onCancel}
              className="glass-button flex-1 bg-gray-500/20 hover:bg-gray-500/30"
            >
              Cancel
            </button>
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default SavingsForm;

import { useState, useEffect, useCallback } from 'react';
import { Loan, SavingsAccount, DashboardData, FinancialSuggestion } from '@/types';
import { storage, autoRefresh } from '@/utils/storage';
import {
  calculateTotalDebt,
  calculateTotalMonthlyEMI,
  generateSuggestions,
  calculateLoanDetails
} from '@/utils/calculations';

/**
 * Custom hook for managing loan data
 */
export const useLoans = () => {
  const [loans, setLoans] = useState<Loan[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadLoans = () => {
      try {
        const storedLoans = storage.getLoans();
        setLoans(storedLoans);
      } catch (error) {
        console.error('Error loading loans:', error);
      } finally {
        setLoading(false);
      }
    };

    loadLoans();
  }, []);

  const addLoan = useCallback((loan: Loan) => {
    const updatedLoans = [...loans, loan];
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const updateLoan = useCallback((loanId: string, updates: Partial<Loan>) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        const updatedLoan = { ...loan, ...updates };
        return updatedLoan;
      }
      return loan;
    });

    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const deleteLoan = useCallback((loanId: string) => {
    const updatedLoans = loans.filter(loan => loan.id !== loanId);
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const addPartPayment = useCallback((loanId: string, partPayment: any) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        const amt = Number(partPayment?.amount) || 0;
        return {
          ...loan,
          partPayments: [...loan.partPayments, { ...partPayment, amount: amt }],
          currentPrincipal: Math.max(0, Number(loan.currentPrincipal) - amt)
        };
      }
      return loan;
    });
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const removeLastPartPayment = useCallback((loanId: string) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        if (loan.partPayments.length === 0) return loan;
        const last = loan.partPayments[loan.partPayments.length - 1];
        const amt = Number(last?.amount) || 0;
        const restored = Number(loan.currentPrincipal) + amt;
        const capped = Math.min(restored, Number(loan.principalAmount));
        return {
          ...loan,
          partPayments: loan.partPayments.slice(0, -1),
          currentPrincipal: capped
        };
      }
      return loan;
    });
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  return {
    loans,
    loading,
    addLoan,
    updateLoan,
    deleteLoan,
    addPartPayment,
    removeLastPartPayment
  };
};

/**
 * Custom hook for managing savings data
 */
export const useSavings = () => {
  const [savings, setSavings] = useState<SavingsAccount[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadSavings = () => {
      try {
        const storedSavings = storage.getSavings();
        setSavings(storedSavings);
      } catch (error) {
        console.error('Error loading savings:', error);
      } finally {
        setLoading(false);
      }
    };

    loadSavings();
  }, []);

  const addSavingsAccount = useCallback((account: SavingsAccount) => {
    const updatedSavings = [...savings, account];
    setSavings(updatedSavings);
    storage.saveSavings(updatedSavings);
  }, [savings]);

  const updateSavingsAccount = useCallback((accountId: string, updates: Partial<SavingsAccount>) => {
    const updatedSavings = savings.map(account =>
      account.id === accountId ? { ...account, ...updates } : account
    );
    setSavings(updatedSavings);
    storage.saveSavings(updatedSavings);
  }, [savings]);

  const deleteSavingsAccount = useCallback((accountId: string) => {
    const updatedSavings = savings.filter(account => account.id !== accountId);
    setSavings(updatedSavings);
    storage.saveSavings(updatedSavings);
  }, [savings]);

  const totalSavings = savings.reduce((total, account) => total + account.amount, 0);

  return {
    savings,
    totalSavings,
    loading,
    addSavingsAccount,
    updateSavingsAccount,
    deleteSavingsAccount
  };
};

/**
 * Custom hook for dashboard data and auto-refresh
 */
export const useDashboard = () => {
  const { loans, addPartPayment, removeLastPartPayment } = useLoans();
  const { savings, totalSavings } = useSavings();
  const [suggestions, setSuggestions] = useState<FinancialSuggestion[]>([]);
  const [lastRefresh, setLastRefresh] = useState<Date | null>(null);

  // Determine next EMI date as the earliest among active loans
  const activeLoans = loans.filter(l => l.isActive);
  const nextEmiDate = activeLoans.length
    ? activeLoans
        .map(l => calculateLoanDetails(l).nextEmiDate)
        .sort((a, b) => a.getTime() - b.getTime())[0]
    : new Date(new Date().getFullYear(), new Date().getMonth(), 5);

  const dashboardData: DashboardData = {
    totalOutstandingDebt: calculateTotalDebt(loans),
    totalSavings,
    monthlyEmi: calculateTotalMonthlyEMI(loans),
    loans,
    savings,
    nextEmiDate
  };

  useEffect(() => {
    // Generate suggestions when loans change
    const newSuggestions = generateSuggestions(loans);
    setSuggestions(newSuggestions);
  }, [loans]);

  useEffect(() => {
    // Check for auto-refresh
    const shouldRefresh = autoRefresh.checkAndRefresh();
    if (shouldRefresh) {
      setLastRefresh(new Date());
      // Trigger any refresh logic here
    }

    const storedLastRefresh = storage.getLastRefresh();
    setLastRefresh(storedLastRefresh);
  }, []);

  return {
    dashboardData,
    suggestions,
    lastRefresh,
    addPartPayment,
    removeLastPartPayment
  };
};

import { useState, useEffect, useCallback } from 'react';
import { Loan, SavingsAccount, DashboardData, FinancialSuggestion } from '@/types';
import { storage, autoRefresh } from '@/utils/storage';
import {
  calculateTotalDebt,
  calculateTotalMonthlyEMI,
  generateSuggestions,
  calculateLoanDetails
} from '@/utils/calculations';

/**
 * Custom hook for managing loan data
 */
export const useLoans = () => {
  const [loans, setLoans] = useState<Loan[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadLoans = () => {
      try {
        const storedLoans = storage.getLoans();
        setLoans(storedLoans);
      } catch (error) {
        console.error('Error loading loans:', error);
      } finally {
        setLoading(false);
      }
    };

    loadLoans();
  }, []);

  const addLoan = useCallback((loan: Loan) => {
    const updatedLoans = [...loans, loan];
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const updateLoan = useCallback((loanId: string, updates: Partial<Loan>) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        const updatedLoan = { ...loan, ...updates };
        return updatedLoan;
      }
      return loan;
    });

    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const deleteLoan = useCallback((loanId: string) => {
    const updatedLoans = loans.filter(loan => loan.id !== loanId);
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const addPartPayment = useCallback((loanId: string, partPayment: any) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        const amt = Number(partPayment?.amount) || 0;
        // Use amortized outstanding as baseline
        const details = calculateLoanDetails(loan);
        const baselineOutstanding = Number(details.remainingPrincipal) || Number(loan.currentPrincipal) || 0;
        return {
          ...loan,
          partPayments: [...loan.partPayments, { ...partPayment, amount: amt }],
          currentPrincipal: Math.max(0, baselineOutstanding - amt)
        };
      }
      return loan;
    });
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  const removeLastPartPayment = useCallback((loanId: string) => {
    const updatedLoans = loans.map(loan => {
      if (loan.id === loanId) {
        if (loan.partPayments.length === 0) return loan;
        const last = loan.partPayments[loan.partPayments.length - 1];
        const amt = Number(last?.amount) || 0;
        const restored = Number(loan.currentPrincipal) + amt;
        const capped = Math.min(restored, Number(loan.principalAmount));
        return {
          ...loan,
          partPayments: loan.partPayments.slice(0, -1),
          currentPrincipal: capped
        };
      }
      return loan;
    });
    setLoans(updatedLoans);
    storage.saveLoans(updatedLoans);
  }, [loans]);

  return {
    loans,
    loading,
    addLoan,
    updateLoan,
    deleteLoan,
    addPartPayment,
    removeLastPartPayment
  };
};

